import React, { useState, useEffect } from 'react';

function App() {
  const [selectedOption, setSelectedOption] = useState('');
  const [tableData, setTableData] = useState([]);

  useEffect(() => {
    if (selectedOption) {
      var url = selectedOption == 'vnexpress' ? `http://localhost:5277/vnexpress` : `https://alpha3-api3.vnexpress.net/api/mobile?type=homev5&app_id=9e304d&offset=0`;
      console.log(url);
      fetch(url, {
        mode: 'no-cors',
        headers: {
          'Content-Type': 'application/json', 
          'Accept': 'application/json',
        }
      })
        .then((response) => response.json)
        // .then((data) => setTableData(data))
        .catch((error) => console.error('Error fetching data:', error));
    }
  }, [selectedOption]);

  const handleSelectChange = (e) => {
    setSelectedOption(e.target.value);
  };

  return (
    <div>
      <h1>Fun Web Scraper</h1>
      <select onChange={handleSelectChange} value={selectedOption}>
        <option value="">Select an option</option>
        <option value="vnexpress">vnexpress</option>
        <option value="tuoitre">tuoitre</option>
      </select>

      {tableData.length > 0 && (
        <table>
          <thead>
            <tr>
              <th>Column 1</th>
              <th>Column 2</th>
              {/* Add more columns as needed */}
            </tr>
          </thead>
          <tbody>
            {tableData.map((row, index) => (
              <tr key={index}>
                <td>{row.column1}</td>
                <td>{row.column2}</td>
                {/* Add more cells as needed */}
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

export default App;
